app.get('/api/my-appointments', authenticateToken, (req, res) => {
  const user_id = req.user.userId;
  
  const sql = `
    SELECT a.*, d.name as doctor_name, d.specialization, d.profile_image 
    FROM appointments a 
    JOIN doctors d ON a.doctor_id = d.id 
    WHERE a.user_id = ? 
    ORDER BY a.appointment_date DESC, a.appointment_time DESC
  `;

  db.all(sql, [user_id], (err, rows) => {
    if (err) return res.status(500).json({ error: err.message });
    res.json({ data: rows });
  });
});

// Get appointments by doctor ID (public, for checking availability)
app.get('/api/doctors/:id/appointments', optionalAuth, (req, res) => {
  const { date } = req.query;
  const params = [req.params.id];
  let sql = "SELECT appointment_date, appointment_time FROM appointments WHERE doctor_id = ? AND status != 'cancelled'";

  if (date) {
    sql += " AND appointment_date = ?";
    params.push(date);
  }

  db.all(sql, params, (err, rows) => {
    if (err) return res.status(500).json({ error: err.message });
    // Only return time slots, not personal info
    const bookedSlots = rows.map(row => ({
      date: row.appointment_date,
      time: row.appointment_time
    }));
    res.json({ data: bookedSlots });
  });
});

// Cancel appointment (requires authentication)
app.patch('/api/appointments/:id/cancel', authenticateToken, (req, res) => {
  const appointmentId = req.params.id;
  const userId = req.user.userId;

  // Verify appointment belongs to user
  db.get("SELECT * FROM appointments WHERE id = ? AND user_id = ?", [appointmentId, userId], (err, appointment) => {
    if (err) return res.status(500).json({ error: err.message });
    if (!appointment) return res.status(404).json({ error: 'Appointment not found or unauthorized' });

    // Update appointment status
    db.run("UPDATE appointments SET status = 'cancelled' WHERE id = ?", [appointmentId], function(err) {
      if (err) return res.status(500).json({ error: err.message });
      res.json({ message: 'Appointment cancelled successfully' });
    });
  });
});

